        of Branch<Element> | Leaf<Element> { ... }

shared class Leaf<Element>(String name, Element element) 
        extends Node<Element>(name) { ... }

shared class Branch<Element>(String name, Node<Element> left, Node<Element> right) 
        extends Node<Element>(name) { ... }

shared Integer add(Integer x, Integer y) {
    return x + y;
}

shared void print(Object... objects) {
    for (Object obj in objects) {
        log.info($obj);
    }
}

shared void addEntry(Key key -> Item item) {
    map.define(key,item);
}

shared Set<Element> singleton<Element>(Element element) 
        given Element satisfies Comparable<Element> {
    return TreeSet(element);
}

void say(String) { ... }

void hello() {
    say("hello");
}

Void goodbye() {
    return say("goodbye");
}

function add(Integer x, Integer y) {
    return x + y;
}

function unit(Boolean floating=false) {
    if (floating) {
        return 1.0;
    }
    else {
        return 1;
    }
}

Comparison order(Value x, Value y);
if (reverseOrder) {
    function reverse(Value x, Value y) { return y<=>x; }
    order = reverse;
}
else {
    function natural(Value x, Value y) { return x<=>y; }
    order = natural;
}

Comparison getOrder()(Integer x, Integer y) {
    return x<=>y;
}

Callable<Comparison,Integer,Integer> getOrder() {
    Comparison order(Integer x, Integer y) { return x<=>y; }
    return order;
}

function fullName(String firstName)(String middleName)(String lastName) {
    return firstName + " " + middleName + " " + lastName;
}

function fullName(String firstName) {
    function fullName2(String middleName) {
        function fullName3(String lastName) {
            return firstName + " " + middleName + " " + lastName;
        }
        return fullName3;
    }
    return fullName2;
}

shared default void writeLine(String line) {
    write(line);
    write("\n");
}

